import { useState } from "react";
import { SuperHero } from "../types";

interface SuperheroDetail {
  superheroInfo: SuperHero;
}

function SuperheroDetails(props: SuperheroDetail) {
  const superheros = props.superheroInfo;

  const [visible, setVisible] = useState(true);

  const [items, setItems] = useState([{ name: "", quantity: "", unit: "" }]);

  const removeElement = () => {
    setVisible((prev) => !prev);
  };

  const deleteItem = (i) => {
    const newItems = [...items];
    newItems.splice(i, 1);
    setItems(newItems);
  };

  return (
    <>
      {items.map((item, i) => {
        return (
          <div key={i} className={`mt3 item-input-wrapper`}>
            <div>some other els here</div>
            <button onClick={() => deleteItem(i)}>delete item</button>
          </div>
        );
      })}

      <div className="test">
        {visible && <button onClick={removeElement}>Remove</button>}

        <h2>{superheros.name}</h2>
        <p>{superheros.description}</p>
      </div>
    </>
  );
}

export default SuperheroDetails;
